export const US_STATES_MAP: MapData = {
  id: 'us',
  name: 'United States',
  viewBox: '0 0 960 600',
  regions: states,
};

const EASY_STATE_IDS = new Set([
  'california', 'texas', 'florida', 'new-york', 'alaska',
  'hawaii', 'montana', 'colorado', 'arizona', 'michigan',
  'illinois', 'ohio', 'pennsylvania', 'georgia', 'washington',
]);

export function getEasyStates(): MapRegion[] {
  return states.filter(s => EASY_STATE_IDS.has(s.id));
}

export function getAllStates(): MapRegion[] {
  return [...states];
}
